sysauto_script.script:
(function executRule(current, previous) {
    var r = new sn_ws.RESTMessageV2('x_847006_cyberint.Cyberint_API', 'Get analysis report');

    r.setStringParameter('alert_id', 'ARG-10');

    var instance = new GlideRecord('x_847006_cyberint_configuration');
    instance.addQuery('api_token', '!=', 0);
    instance.query();
    instance.next();

    var decrypted = instance.api_token.getDecryptedValue();
    r.setStringParameter('api_environment', instance.api_environment);
    r.setRequestHeader("Cookie", "access_token=" + decrypted);
    r.saveResponseBodyAsAttachment('x_847006_cyberint_cyberint_alerts', '3d4c7c49974211108e05fbc71153afa8', 'ARG-10_file.pdf');

    var response = r.execute();
    var httpStatus = response.getStatusCode();

    gs.info(" *** GET analysis report - Response Status: " + httpStatus);

})(current, previous);
