sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    if (current.status != "open") {
        if (current.status == 'acknowledged' || (current.status == 'closed' && current.closing_reason)) {
            try {
                var r = new sn_ws.RESTMessageV2('x_847006_cyberint.Cyberint_Puller', 'PUT - Update Alerts in CyberInt');

                var instance = new GlideRecord('x_847006_cyberint_configuration');

                instance.addQuery('api_token', '!=', 0);
                instance.query();
                instance.next();

                gs.info("PUT instance : " + instance.instance_name);
                r.setStringParameter('api_environment', instance.api_environment);
                var decrypted = instance.api_token.getDecryptedValue();

                r.setRequestHeader("Cookie", "access_token=" + decrypted);

                var data = {
                    "status": String(current.status),
                    "closure_reason": String(current.closing_reason)
                };

                if (current.status == 'acknowledged') {
                    data = {
                        "status": String(current.status)
                    };
                }

                var body = {
                    "alert_ref_ids": [String(current.alert_id)],
                    "data": data
                };

                r.setRequestBody(JSON.stringify(body));

                var RequestBody = r.getRequestBody();
                var RequestHeader = r.getRequestHeader('Cookie');
                var response = r.execute();
                gs.info(instance.instance_name + " - Close Alert " + current.alert_id + " RequestBody : " + RequestBody);

                var httpStatus = response.getStatusCode();
                var responseBody = response.getBody();
                gs.info(instance.instance_name + " - Close Alert " + current.alert_id + " Response Status (Close Alert) : " + httpStatus + ", Response Body : " + responseBody);
            } catch (ex) {
                var message = ex.message;
                gs.info(message);

            }
        } else {
            gs.info(instance.instance_name + " - Close Alert = Alert not update");
        }
    }

})(current, previous);
