sysauto_script.script:
(function executRule(current, previous) {
    var r = new sn_ws.RESTMessageV2('x_847006_cyberint.Cyberint_Puller', 'Get analysis report');

    r.setStringParameter('alert_id', 'ARG-10');

    var instance = new GlideRecord('x_847006_cyberint_configuration');
    instance.addQuery('api_token', '!=', 0);
    instance.query();
    instance.next();

	var decrypted = instance.api_token.getDecryptedValue();
	r.setStringParameter('api_environment', instance.api_environment);
    r.setRequestHeader("Cookie", "access_token=" + decrypted);

    var response = r.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();

    gs.info(instance.instance_name + " - GET analysis report - Response Status: " + httpStatus);
    gs.info("GET analysis report - Response Body: " + responseBody);


    var alert = new GlideRecord('x_847006_cyberint_cyberint_alerts');

    alert.addQuery('alert_id', '=', 'ARG-10');
    alert.query();
    exist_alert = alert.next();
	gs.info(instance.instance_name + " - analysis report = alert_id "+alert.alert_id);
    alert.attachment = responseBody;
    alert.source = "";
    alert.update();

})(current, previous);
